import React from "react";
import { addList } from "../actions";
import { connect } from "react-redux";

class CreateList extends React.Component {

  state = { term: "", inputHolder: "Add To-do Data",updateHolder: "Update To-do Data" };


  onInputChange = event => {
    this.setState({ term: event.target.value });
  };

  onSearchSubmit = event => {
    event.preventDefault();

    //send this value to reducer using action creator
    if (this.state.term !== "") {
      this.props.addList(this.state.term);
      this.setState({ term: "" });
    } else {
      alert("enter To Do item");
    }
  };

  onUpdateSubmit = event => {
    event.preventDefault();

/* 
    return this.props.mydata.map((data, id) => {
      return (
        <div>a</div>
        )
      }) */

    //send this value to reducer using action creator
   /*  if (this.state.term !== "") {
      this.props.addList(this.state.term);
      this.setState({ term: "" });
    } else {
      alert("enter To Do item");
    } */
  };

  oldVal(){
    return (
      this.setState({term:this.props.triggerChildUpdate.editkey})
    )
  }

  listForm() {
    if(this.props.triggerChildUpdate.editTable){
    return (

      <div className=" search-bar ui segment">
        <form onSubmit={this.onUpdateSubmit} className="ui form">
          <div className="field">
            <label>Update To Do Item</label>
            <input 
              id="textData"
              type="text"
              value={this.oldVal}
              placeholder={this.state.updateHolder}
              onChange={this.onInputChange}
            />
          </div>
        </form>
      </div>
    );
    }else{
      return (
        <div className=" search-bar ui segment">
          <form onSubmit={this.onSearchSubmit} className="ui form">
            <div className="field">
              <label>Add To Do Item</label>
              <input 
                id="textData"
                type="text"
                value={this.state.term}
                placeholder={this.state.inputHolder}
                onChange={this.onInputChange}
              />
            </div>
          </form>
        </div>
      );
    }

  }
  render() {
    console.log(this.props.triggerChildUpdate,"clist")
    return <div className="ui relaxed divided list">{this.listForm()}</div>;
  }
}

const mapStateToProps = state => {
  //console.log(state)
  return {
    mydata: state.mydata
  };
};

export default connect(
  mapStateToProps,
  { addList }
)(CreateList);
