import React from "react";
import CreateList from "./CreateList";
import ShowList from "./ShowList";

class App extends React.Component {
  state = { editTable: false,editKey:''};

 /* function that get the value from child component for editing */
  getUpdateKey = val =>{
   //console.log(val);
    /* to set the dom for edit mode */
   if(val !== undefined){
     this.setState({editTable:true,editKey:val});
   }
  }

  /* function for sendind key data to createList component for editing */
  /* setUpdateOnkey = () => {
    if(this.state.editTable){
      console.log(this.state.editKey)
    }
  } */


  render(){
    return (
    <div className="ui container">
      <CreateList childUpdate={this.state.editKey}/>
      <ShowList triggerParentUpdate={this.getUpdateKey}/>
    </div>
  )};
};

export default App;
